import os
import svgwrite
import cairosvg
from textwrap import wrap
from PIL import Image, ImageFont, ImageDraw
from io import BytesIO

# base64-код вашего шрифта Oldtimer
base64_font = """"""



def create_svg_gradient_background(dwg, width, height, color1, color2):
    gradient = dwg.linearGradient(start=(0, 0), end=(0, 1), id="grad")
    gradient.add_stop_color(offset=0, color=color1)
    gradient.add_stop_color(offset=1, color=color2)
    dwg.defs.add(gradient)
    dwg.add(dwg.rect(insert=(0, 0), size=(width, height), fill="url(#grad)"))

def get_text_size(text, font):
    """Вычисление размера текста в пикселях с использованием PIL"""
    image = Image.new('RGB', (1, 1))
    draw = ImageDraw.Draw(image)
    bbox = draw.textbbox((0, 0), text, font=font)
    return bbox[2] - bbox[0], bbox[3] - bbox[1]

def wrap_text_svg(text, font, max_width):
    """Функция для переноса текста на несколько строк, если он не помещается в одну строку"""
    lines = []
    words = text.split()
    while words:
        line = ''
        while words and (get_text_size(line + words[0], font)[0] <= max_width):
            line += (words.pop(0) + ' ')
        if not line:  # Если слово слишком длинное и не вмещается на одной строке
            line = words.pop(0)
        lines.append(line.strip())
    return lines

def fit_text_to_cell(text, font_path, max_width, max_height):
    """Функция для автоматической подгонки текста под размеры ячейки"""
    font_size = 22
    while font_size > 5:  # Ограничение на минимальный размер шрифта
        font = ImageFont.truetype(font_path, font_size)
        lines = wrap_text_svg(text, font, max_width)
        line_height = get_text_size('A', font)[1] + 5  # Добавляем 5 пикселей между строками
        total_height = len(lines) * line_height
        
        if total_height <= max_height and all(get_text_size(line, font)[0] <= max_width for line in lines):
            return lines, font_size
        font_size -= 1
    return wrap_text_svg(text, ImageFont.truetype(font_path, font_size), max_width), font_size

def create_bingo_card(title, elements, output_filename, gradient_colors=("lightblue", "lightpink"), table_bg_color="white", main_border_width=5, inner_border_width=2, corner_radius=30, font_path="Oldtimer.ttf"):
    size = int(len(elements) ** 0.5)  # Определяем размер таблицы (например, 5x5)
    assert size * size == len(elements), "Количество элементов должно быть квадратом целого числа."

    # Настраиваем размеры
    cell_size = 200  # Размер ячейки
    table_width = cell_size * size
    table_height = cell_size * size
    img_width = table_width + 200
    img_height = table_height + 200

    # Загружаем шрифт для заголовка
    font_title = ImageFont.truetype(font_path, 50)

    # Создаем SVG документ с полным профилем
    dwg = svgwrite.Drawing(output_filename, profile='full', size=(img_width, img_height))
    
    # Вставляем шрифт в документ SVG
    font_face = f"""
    @font-face {{
        font-family: 'Oldtimer';
        src: url('data:font/truetype;charset=utf-8;base64,{base64_font}') format('truetype');
    }}
    """
    style = dwg.style(content=font_face)
    dwg.defs.add(style)

    # Создаем градиентный фон
    create_svg_gradient_background(dwg, img_width, img_height, gradient_colors[0], gradient_colors[1])

    # Добавляем заголовок
    title_font_size = 50
    dwg.add(dwg.text(title, insert=(img_width // 2, 70), text_anchor="middle", font_size=title_font_size, font_family="Oldtimer", fill="black"))

    # Создаем таблицу с закругленными углами
    table_x = 100
    table_y = 100
    table = dwg.add(dwg.g(id='table', fill='none'))
    table.add(dwg.rect(insert=(table_x, table_y), size=(table_width, table_height), fill=table_bg_color, stroke="none", rx=corner_radius, ry=corner_radius))

    # Рисуем сетку бинго и добавляем элементы
    for i in range(size):
        for j in range(size):
            x0 = table_x + j * cell_size
            y0 = table_y + i * cell_size
            if i == 0 and j == 0:  # Верхний левый угол
                path = dwg.path(d=f"M {x0 + corner_radius},{y0} h {cell_size - corner_radius} v {cell_size} h -{cell_size} v -{cell_size - corner_radius} a {corner_radius},{corner_radius} 0 0,1 {corner_radius},{-corner_radius} z", fill="none", stroke="black", stroke_width=inner_border_width)
            elif i == 0 and j == size - 1:  # Верхний правый угол
                path = dwg.path(d=f"M {x0},{y0} h {cell_size - corner_radius} a {corner_radius},{corner_radius} 0 0,1 {corner_radius},{corner_radius} v {cell_size - corner_radius} h -{cell_size} v -{cell_size} z", fill="none", stroke="black", stroke_width=inner_border_width)
            elif i == size - 1 and j == 0:  # Нижний левый угол
                path = dwg.path(d=f"M {x0},{y0} v {cell_size - corner_radius} a {corner_radius},{corner_radius} 0 0,0 {corner_radius},{corner_radius} h {cell_size - corner_radius} v -{cell_size} h -{cell_size} z", fill="none", stroke="black", stroke_width=inner_border_width)
            elif i == size - 1 and j == size - 1:  # Нижний правый угол
                path = dwg.path(d=f"M {x0+cell_size},{y0} v {cell_size - corner_radius} a {corner_radius},{corner_radius} 0 0,1 -{corner_radius},{corner_radius} h -{cell_size - corner_radius} v -{cell_size} h {cell_size} z", fill="none", stroke="black", stroke_width=inner_border_width)
            else:
                path = dwg.rect(insert=(x0, y0), size=(cell_size, cell_size), fill="none", stroke="black", stroke_width=inner_border_width)
            table.add(path)
            
            text = elements[i * size + j]
            lines, font_size = fit_text_to_cell(text, font_path, cell_size - 20, cell_size - 20)
            line_height = get_text_size('A', ImageFont.truetype(font_path, font_size))[1] + 5
            for k, line in enumerate(lines):
                table.add(dwg.text(line, insert=(x0 + cell_size / 2, y0 + (cell_size / 2) - ((len(lines) - 1) * line_height / 2) + k * line_height), text_anchor="middle", font_size=font_size, font_family="Oldtimer", fill="black"))

    # Сохраняем SVG файл
    dwg.save()
    print(f"Карточка бинго сохранена в {output_filename}")
    
    
# Пример использования
